import Header from "../components/Header";
import { useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();

  return (
    <>
      <Header />

      <div style={{ padding: "30px" }}>
        {/* Welcome Section */}
        <div
          style={{
            background: "linear-gradient(135deg, #2c5364, #203a43)",
            color: "#fff",
            padding: "30px",
            borderRadius: "16px",
            marginBottom: "30px",
          }}
        >
          <h1 style={{ marginBottom: "10px" }}>
            Welcome to AI Health Assistant
          </h1>
          <p>Your smart companion for health guidance and medical support.</p>
        </div>

        {/* Dashboard Cards */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
            gap: "20px",
          }}
        >
          {/* Health Chat */}
          <DashboardCard
            title="AI Health Chat"
            description="Chat with AI for health guidance"
            onClick={() => navigate("/health-chat")}
          />

          {/* Nearby Hospitals */}
          <DashboardCard
            title="Nearby Hospitals"
            description="Find hospitals and doctors near you"
            onClick={() => navigate("/nearby")}
          />

          {/* Emergency */}
          <DashboardCard
            title="Emergency Help"
            description="Quick access to emergency assistance"
            danger
            onClick={() => alert("Emergency feature coming soon")}
          />

          {/* Profile */}
          <DashboardCard
            title="Profile"
            description="View account and role details"
            onClick={() => alert("Profile page coming soon")}
          />
        </div>
      </div>
    </>
  );
}

function DashboardCard({ title, description, onClick, danger }) {
  return (
    <div
      onClick={onClick}
      style={{
        padding: "24px",
        borderRadius: "14px",
        backgroundColor: danger ? "#ffecec" : "#ffffff",
        border: danger ? "1px solid #ffb3b3" : "1px solid #ddd",
        boxShadow: "0 6px 18px rgba(0,0,0,0.08)",
        cursor: "pointer",
        transition: "transform 0.2s ease",
      }}
      onMouseEnter={(e) =>
        (e.currentTarget.style.transform = "translateY(-4px)")
      }
      onMouseLeave={(e) => (e.currentTarget.style.transform = "translateY(0)")}
    >
      <h3 style={{ marginBottom: "10px" }}>{title}</h3>
      <p style={{ color: "#555" }}>{description}</p>
    </div>
  );
}

export default Home;
