import { createContext, useEffect, useState } from "react";
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut,
  GoogleAuthProvider,
  signInWithPopup,
  onAuthStateChanged,
} from "firebase/auth";
import { auth } from "../firebase";

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        const role =
          currentUser.email === "admin@system.com" ? "admin" : "user";

        setUser({
          email: currentUser.email,
          role,
        });
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsub();
  }, []);

  const login = async (email, password) => {
    const res = await signInWithEmailAndPassword(auth, email, password);

    const role = res.user.email === "admin@system.com" ? "admin" : "user";

    return { email: res.user.email, role };
  };

  const signup = async (email, password) => {
    const res = await createUserWithEmailAndPassword(auth, email, password);
    return { email: res.user.email, role: "user" };
  };

  const googleLogin = async () => {
    const provider = new GoogleAuthProvider();
    const res = await signInWithPopup(auth, provider);

    const role = res.user.email === "admin@system.com" ? "admin" : "user";

    return { email: res.user.email, role };
  };

  const logout = async () => {
    await signOut(auth);
  };

  return (
    <AuthContext.Provider value={{ user, login, signup, googleLogin, logout }}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
