import { useState, useRef, useEffect } from "react";
import Header from "../components/Header";

function HealthChat() {
  const [messages, setMessages] = useState([
    {
      sender: "ai",
      text: "Hello! I am your AI Health Assistant. I provide general health information, not medical diagnosis. How can I help you?",
    },
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const response = await fetch("http://localhost:5000/api/health-chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message: userMessage.text }),
      });

      const data = await response.json();

      setMessages((prev) => [...prev, { sender: "ai", text: data.reply }]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        {
          sender: "ai",
          text: "There was an error connecting to the AI service. Please try again later.",
        },
      ]);
    }

    setLoading(false);
  };

  return (
    <>
      <Header />

      <div
        style={{
          maxWidth: "900px",
          margin: "0 auto",
          height: "calc(100vh - 60px)",
          display: "flex",
          flexDirection: "column",
          padding: "20px",
        }}
      >
        <h2>Health AI Chat</h2>

        <div
          style={{
            flex: 1,
            overflowY: "auto",
            padding: "15px",
            border: "1px solid #ddd",
            borderRadius: "12px",
            backgroundColor: "#f9f9f9",
          }}
        >
          {messages.map((msg, index) => (
            <div
              key={index}
              style={{
                display: "flex",
                justifyContent:
                  msg.sender === "user" ? "flex-end" : "flex-start",
                marginBottom: "10px",
              }}
            >
              <div
                style={{
                  maxWidth: "70%",
                  padding: "12px 16px",
                  borderRadius: "16px",
                  backgroundColor:
                    msg.sender === "user" ? "#2c5364" : "#e0e0e0",
                  color: msg.sender === "user" ? "#fff" : "#000",
                }}
              >
                {msg.text}
              </div>
            </div>
          ))}

          {loading && (
            <div style={{ color: "#555", fontStyle: "italic" }}>
              AI is typing...
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        <div style={{ display: "flex", marginTop: "15px", gap: "10px" }}>
          <input
            type="text"
            placeholder="Ask a health-related question..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            style={{
              flex: 1,
              padding: "12px",
              borderRadius: "8px",
              border: "1px solid #ccc",
            }}
          />

          <button
            onClick={handleSend}
            disabled={loading}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              backgroundColor: "#2c5364",
              color: "#fff",
              cursor: "pointer",
            }}
          >
            Send
          </button>
        </div>
      </div>
    </>
  );
}

export default HealthChat;
