import { useLocation } from "react-router-dom";
import { useState } from "react";

export default function Map() {
  const { state } = useLocation();
  const hospital = state?.hospital;
  const user = state?.userLocation;

  const [mode, setMode] = useState("hospital");

  let mapUrl = "https://www.openstreetmap.org/export/embed.html";

  if (mode === "hospital" && hospital) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?marker=${hospital.lat},${hospital.lon}&zoom=15`;
  }

  if (mode === "user" && user) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?marker=${user.lat},${user.lon}&zoom=15`;
  }

  if (mode === "both" && hospital && user) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?bbox=${Math.min(
      hospital.lon,
      user.lon
    )},${Math.min(hospital.lat, user.lat)},${Math.max(
      hospital.lon,
      user.lon
    )},${Math.max(hospital.lat, user.lat)}&marker=${hospital.lat},${
      hospital.lon
    }`;
  }

  return (
    <div style={{ padding: "16px" }}>
      <h2>Map View</h2>

      {hospital && (
        <>
          <p>
            <strong>{hospital.name}</strong>
          </p>
          <p>{hospital.address}</p>
          <button onClick={() => setMode("hospital")}>
            Hospital Location
          </button>{" "}
          <button onClick={() => setMode("user")}>My Location</button>{" "}
          <button onClick={() => setMode("both")}>Both</button>
        </>
      )}

      <iframe
        title="map"
        width="100%"
        height="450"
        frameBorder="0"
        src={mapUrl}
        style={{ marginTop: "10px", borderRadius: "10px" }}
      />

      {hospital && (
        <p style={{ marginTop: "10px" }}>
          <a
            href={`https://www.google.com/maps/dir/?api=1&destination=${hospital.lat},${hospital.lon}`}
            target="_blank"
            rel="noreferrer"
          >
            Open Route in Google Maps
          </a>
        </p>
      )}
    </div>
  );
}
