import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";

export default function Signup({ setPage }) {
  const { signup, googleLogin } = useContext(AuthContext);

  const [firstName, setFirstName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({}); // validation state

  const validate = () => {
    const newErrors = {};

    if (!firstName.trim()) {
      newErrors.firstName = "First name is required";
    }

    if (!email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      newErrors.email = "Please enter a valid email address";
    }

    if (!password) {
      newErrors.password = "Password is required";
    } else if (password.length < 6) {
      newErrors.password = "Password must be at least 6 characters long";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSignup = async () => {
    if (!validate()) return;

    try {
      await signup(email, password, firstName);
      setPage("home");
    } catch {
      alert("Signup failed — please try again");
    }
  };

  return (
    <div className="auth-page">
      <h2>Create Account</h2>
      <p className="auth-subtitle">Create your account to continue</p>

      <div className="form-group">
        <input
          type="text"
          placeholder="First name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        {errors.firstName && <p className="error-text">{errors.firstName}</p>}
      </div>

      <div className="form-group">
        <input
          type="email"
          placeholder="Email address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {errors.email && <p className="error-text">{errors.email}</p>}
      </div>

      <div className="form-group">
        <input
          type={showPassword ? "text" : "password"}
          placeholder="Create password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {errors.password && <p className="error-text">{errors.password}</p>}
      </div>

      <div className="show-password-row">
        <label>
          <input
            type="checkbox"
            checked={showPassword}
            onChange={() => setShowPassword(!showPassword)}
          />
          <span>Show password</span>
        </label>
      </div>

      <button className="auth-btn primary" onClick={handleSignup}>
        Sign up
      </button>

      <button className="auth-btn google" onClick={googleLogin}>
        Sign up with Google
      </button>

      <div className="auth-footer">
        Already have an account?{" "}
        <button onClick={() => setPage("login")}>Login</button>
      </div>
    </div>
  );
}
