import { useState } from "react";
import Header from "../components/Header";

function HealthChat() {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([
    {
      from: "bot",
      text: "Hello! I am your Health Assistant. I provide general health guidance (not medical diagnosis). How can I help you?",
    },
  ]);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const userMessage = { from: "user", text: message };
    setMessages((prev) => [...prev, userMessage]);
    setMessage("");

    try {
      const res = await fetch("http://localhost:5000/api/health-guidance", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message }),
      });

      const data = await res.json();

      setMessages((prev) => [...prev, { from: "bot", text: data.reply }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        {
          from: "bot",
          text: "Unable to provide guidance right now. Please try again.",
        },
      ]);
    }
  };

  return (
    <>
      <Header />
      <div style={{ padding: "20px" }}>
        <h2>Health Guidance</h2>

        <div
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            borderRadius: "8px",
            minHeight: "300px",
            marginBottom: "10px",
          }}
        >
          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                textAlign: m.from === "user" ? "right" : "left",
                marginBottom: "8px",
              }}
            >
              <span
                style={{
                  display: "inline-block",
                  padding: "10px",
                  borderRadius: "8px",
                  background: m.from === "user" ? "#2c5364" : "#eee",
                  color: m.from === "user" ? "#fff" : "#000",
                }}
              >
                {m.text}
              </span>
            </div>
          ))}
        </div>

        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Describe your symptoms..."
          style={{ width: "80%", padding: "10px" }}
        />
        <button
          onClick={sendMessage}
          style={{ padding: "10px 20px", marginLeft: "10px" }}
        >
          Send
        </button>
      </div>
    </>
  );
}

export default HealthChat;
