import { useState, useRef, useEffect } from "react";
import Header from "../components/Header";

function HealthChat() {
  const [messages, setMessages] = useState([
    {
      sender: "ai",
      text: "Hello! I am your AI Health Assistant. How can I help you today?",
    },
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage = {
      sender: "user",
      text: input,
    };

    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    // Simulated AI response
    setTimeout(() => {
      const aiMessage = {
        sender: "ai",
        text: "Thank you for your question. Based on general health knowledge, I recommend consulting a doctor for accurate diagnosis.",
      };
      setMessages((prev) => [...prev, aiMessage]);
    }, 800);
  };

  return (
    <>
      <Header />

      <div
        style={{
          maxWidth: "900px",
          margin: "0 auto",
          height: "calc(100vh - 60px)",
          display: "flex",
          flexDirection: "column",
          padding: "20px",
        }}
      >
        <h2 style={{ marginBottom: "10px" }}>Health AI Chat</h2>

        {/* Chat Area */}
        <div
          style={{
            flex: 1,
            overflowY: "auto",
            padding: "15px",
            border: "1px solid #ddd",
            borderRadius: "12px",
            backgroundColor: "#f9f9f9",
          }}
        >
          {messages.map((msg, index) => (
            <div
              key={index}
              style={{
                display: "flex",
                justifyContent:
                  msg.sender === "user" ? "flex-end" : "flex-start",
                marginBottom: "10px",
              }}
            >
              <div
                style={{
                  maxWidth: "70%",
                  padding: "12px 16px",
                  borderRadius: "16px",
                  backgroundColor:
                    msg.sender === "user" ? "#2c5364" : "#e0e0e0",
                  color: msg.sender === "user" ? "#fff" : "#000",
                }}
              >
                {msg.text}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Area */}
        <div
          style={{
            display: "flex",
            marginTop: "15px",
            gap: "10px",
          }}
        >
          <input
            type="text"
            placeholder="Ask a health-related question..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            style={{
              flex: 1,
              padding: "12px",
              borderRadius: "8px",
              border: "1px solid #ccc",
            }}
          />

          <button
            onClick={handleSend}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              backgroundColor: "#2c5364",
              color: "#fff",
              cursor: "pointer",
            }}
          >
            Send
          </button>
        </div>
      </div>
    </>
  );
}

export default HealthChat;
