import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

export default function Map() {
  const location = useLocation();
  const hospital = location.state?.hospital;

  const [userPos, setUserPos] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setUserPos({
          lat: pos.coords.latitude,
          lon: pos.coords.longitude,
        });
      },
      () => {
        setUserPos(null);
      }
    );
  }, []);

  let mapUrl = "https://www.openstreetmap.org/export/embed.html";

  if (hospital && userPos) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?bbox=${
      userPos.lon - 0.05
    },${userPos.lat - 0.05},${hospital.lon + 0.05},${
      hospital.lat + 0.05
    }&marker=${hospital.lat},${hospital.lon}`;
  } else if (hospital) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?marker=${hospital.lat},${hospital.lon}`;
  }

  return (
    <div style={{ padding: "16px" }}>
      <h2>Map View</h2>

      {hospital ? (
        <div style={{ marginBottom: "10px" }}>
          <strong>{hospital.name}</strong>
          <p>{hospital.address || "Address not available"}</p>
          <p>{hospital.phone || "Phone not available"}</p>
        </div>
      ) : (
        <p>No location selected</p>
      )}

      <iframe
        title="map"
        width="100%"
        height="450"
        frameBorder="0"
        src={mapUrl}
        style={{ borderRadius: "10px" }}
      />

      {hospital && (
        <div style={{ marginTop: "10px" }}>
          <a
            href={`https://www.google.com/maps/dir/?api=1&destination=${hospital.lat},${hospital.lon}`}
            target="_blank"
            rel="noreferrer"
          >
            Open Route in Google Maps
          </a>
        </div>
      )}
    </div>
  );
}
