import React, { useState } from "react";

function AppointmentLetter() {
  const [patientName, setPatientName] = useState("");
  const [date, setDate] = useState("");
  const [doctor, setDoctor] = useState("");
  const [reason, setReason] = useState("");
  const [letter, setLetter] = useState("");

  const generateLetter = () => {
    if (!patientName || !date || !doctor || !reason) {
      alert("Please fill all fields");
      return;
    }

    const content = `
APPOINTMENT LETTER

Patient Name: ${patientName}
Appointment Date: ${date}
Doctor / Hospital: ${doctor}
Reason for Visit: ${reason}

This letter confirms the above appointment.

Disclaimer:
This appointment letter is generated for educational purposes only and
does not confirm medical consultation or treatment.
`;

    setLetter(content);
  };

  const downloadText = () => {
    const blob = new Blob([letter], { type: "text/plain" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "appointment-letter.txt";
    a.click();

    URL.revokeObjectURL(url);
  };

  const downloadPDF = () => {
    window.print();
  };

  return (
    <div className="page">
      <h2>Appointment Letter Generator</h2>

      <input
        placeholder="Patient Name"
        value={patientName}
        onChange={(e) => setPatientName(e.target.value)}
      />

      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />

      <input
        placeholder="Doctor / Hospital Name"
        value={doctor}
        onChange={(e) => setDoctor(e.target.value)}
      />

      <textarea
        placeholder="Reason for Visit"
        value={reason}
        onChange={(e) => setReason(e.target.value)}
      />

      <button onClick={generateLetter}>Generate Letter</button>

      {letter && (
        <div className="result">
          <pre>{letter}</pre>

          <button onClick={downloadText}>Download as Text</button>
          <button onClick={downloadPDF}>Download as PDF</button>
        </div>
      )}

      <p className="disclaimer">
        This letter is for educational purposes only.
      </p>
    </div>
  );
}

export default AppointmentLetter;
