function Home() {
  // Mock dynamic data (will be API-driven later)
  const hospitalUpdates = [
    {
      id: 1,
      title: "Free Health Camp",
      description:
        "Free general health check-up camp at City General Hospital today.",
      date: "Today",
    },
    {
      id: 2,
      title: "OPD Timing Update",
      description: "OPD timing changed to 9 AM ‚Äì 3 PM at Government Hospital.",
      date: "Today",
    },
    {
      id: 3,
      title: "New Cardiology Wing",
      description: "New cardiology department opened at Apollo Hospital.",
      date: "Yesterday",
    },
  ];

  const healthTips = [
    "Drink at least 2‚Äì3 liters of water daily.",
    "Wash hands frequently to prevent infections.",
    "Do not ignore chest pain or breathing difficulty.",
    "Regular health check-ups can prevent major diseases.",
  ];

  const featuredHospitals = [
    {
      name: "Apollo Hospital",
      rating: 4.5,
      contact: "+91 98765 43210",
    },
    {
      name: "City General Hospital",
      rating: 4.2,
      contact: "+91 91234 56789",
    },
  ];

  return (
    <div style={{ padding: "30px" }}>
      {/* Welcome Section */}
      <h1>Welcome to AI Health Assistant</h1>
      <p style={{ color: "#555", marginBottom: "30px" }}>
        Your trusted platform for health guidance, hospital information, and
        emergency support.
      </p>

      {/* Hospital Updates */}
      <section style={{ marginBottom: "40px" }}>
        <h2>üè• Daily Hospital Updates</h2>
        {hospitalUpdates.map((item) => (
          <div
            key={item.id}
            style={{
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "8px",
              marginTop: "10px",
              backgroundColor: "#f9fafb",
            }}
          >
            <strong>{item.title}</strong>
            <p>{item.description}</p>
            <small style={{ color: "#777" }}>{item.date}</small>
          </div>
        ))}
      </section>

      {/* Health Awareness */}
      <section style={{ marginBottom: "40px" }}>
        <h2>üí° Health Awareness</h2>
        <ul>
          {healthTips.map((tip, index) => (
            <li key={index} style={{ marginBottom: "8px" }}>
              {tip}
            </li>
          ))}
        </ul>
      </section>

      {/* Featured Hospitals */}
      <section>
        <h2>‚≠ê Featured Hospitals</h2>
        {featuredHospitals.map((hospital, index) => (
          <div
            key={index}
            style={{
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "8px",
              marginTop: "10px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <div>
              <strong>{hospital.name}</strong>
              <p>Rating: ‚≠ê {hospital.rating}</p>
            </div>
            <div>
              <p>üìû {hospital.contact}</p>
            </div>
          </div>
        ))}
      </section>
    </div>
  );
}

export default Home;
