import { useState } from "react";

export default function Map() {
  const [coords, setCoords] = useState(null);
  const [error, setError] = useState("");

  const getLiveLocation = () => {
    if (!navigator.geolocation) {
      setError("Geolocation not supported");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setCoords({
          lat: pos.coords.latitude,
          lon: pos.coords.longitude,
        });
        setError("");
      },
      () => {
        setError("Location permission denied");
      }
    );
  };

  return (
    <div className="container">
      <h2>Map View</h2>

      <button onClick={getLiveLocation}>üìç Show My Location on Map</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {coords && (
        <div style={{ marginTop: "20px" }}>
          <iframe
            title="OpenStreetMap"
            width="100%"
            height="450"
            style={{ border: 0 }}
            src={`https://www.openstreetmap.org/export/embed.html?bbox=${
              coords.lon - 0.02
            }%2C${coords.lat - 0.02}%2C${coords.lon + 0.02}%2C${
              coords.lat + 0.02
            }&layer=mapnik&marker=${coords.lat}%2C${coords.lon}`}
          />

          <p style={{ marginTop: "10px" }}>
            <a
              href={`https://www.openstreetmap.org/?mlat=${coords.lat}&mlon=${coords.lon}#map=16/${coords.lat}/${coords.lon}`}
              target="_blank"
              rel="noreferrer"
            >
              üó∫ Open Full Map
            </a>
          </p>
        </div>
      )}
    </div>
  );
}
