import { useState } from "react";

export default function NearbyFinder() {
  const [location, setLocation] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [selectedType, setSelectedType] = useState("All");

  // ‚úÖ NEW: user live location
  const [userCoords, setUserCoords] = useState(null);

  // =========================
  // Utility: distance in KM
  // =========================
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const toRad = (v) => (v * Math.PI) / 180;
    const R = 6371; // Earth radius (km)

    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);

    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;

    return (R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a))).toFixed(2);
  };

  // =========================
  // Get live user location
  // =========================
  const getLiveLocation = () => {
    if (!navigator.geolocation) {
      setError("Geolocation is not supported by this browser");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setUserCoords({
          lat: pos.coords.latitude,
          lon: pos.coords.longitude,
        });
        setError("");
      },
      () => {
        setError("Location permission denied");
      }
    );
  };

  // =========================
  // Find nearby facilities
  // =========================
  const findNearby = async () => {
    if (!location.trim() && !userCoords) {
      setError("Enter a location or use live location");
      return;
    }

    setLoading(true);
    setError("");
    setResults([]);

    try {
      let lat, lon;

      // If live location exists, use it
      if (userCoords) {
        lat = userCoords.lat;
        lon = userCoords.lon;
      } else {
        const geoRes = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
            location
          )}`
        );
        const geoData = await geoRes.json();

        if (!geoData.length) {
          throw new Error("Location not found");
        }

        lat = geoData[0].lat;
        lon = geoData[0].lon;
      }

      const query = `
        [out:json];
        (
          node["amenity"="hospital"](around:8000,${lat},${lon});
          way["amenity"="hospital"](around:8000,${lat},${lon});
          node["amenity"="clinic"](around:8000,${lat},${lon});
          way["amenity"="clinic"](around:8000,${lat},${lon});
          node["amenity"="pharmacy"](around:8000,${lat},${lon});
          way["amenity"="pharmacy"](around:8000,${lat},${lon});
        );
        out center tags;
      `;

      const overpassUrl =
        "https://overpass.kumi.systems/api/interpreter?data=" +
        encodeURIComponent(query);

      const overpassRes = await fetch(overpassUrl);
      const overpassData = await overpassRes.json();

      const facilities = overpassData.elements.map((item) => {
        const type =
          item.tags?.amenity === "hospital"
            ? "Hospital"
            : item.tags?.amenity === "clinic"
            ? "Clinic"
            : "Pharmacy";

        const fLat = item.lat || item.center?.lat;
        const fLon = item.lon || item.center?.lon;

        const distance =
          userCoords && fLat && fLon
            ? calculateDistance(userCoords.lat, userCoords.lon, fLat, fLon)
            : null;

        return {
          name: item.tags?.name || "Unnamed Facility",
          type,
          phone:
            item.tags?.phone || item.tags?.["contact:phone"] || "Not available",
          address:
            item.tags?.["addr:full"] ||
            `${item.tags?.["addr:street"] || ""} ${
              item.tags?.["addr:city"] || ""
            }`.trim() ||
            "Address not available",
          distance,
        };
      });

      setResults(facilities);
      setSelectedType("All");
    } catch (err) {
      setError(err.message);
    }

    setLoading(false);
  };

  // =========================
  // Filters & styles
  // =========================
  const filteredResults =
    selectedType === "All"
      ? results
      : results.filter((r) => r.type === selectedType);

  const tabStyle = (type) => ({
    padding: "6px 14px",
    marginRight: "8px",
    borderRadius: "20px",
    border: "1px solid #ddd",
    cursor: "pointer",
    backgroundColor: selectedType === type ? "#0d6efd" : "#f8f9fa",
    color: selectedType === type ? "#fff" : "#000",
  });

  const badgeColor = (type) =>
    type === "Hospital" ? "#dc3545" : type === "Clinic" ? "#0d6efd" : "#198754";

  // =========================
  // UI
  // =========================
  return (
    <div className="container">
      <h2>Nearby Doctor / Hospital Finder</h2>

      <input
        type="text"
        placeholder="Enter city / area / pincode"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />

      <div style={{ marginTop: "8px" }}>
        <button onClick={findNearby} disabled={loading}>
          {loading ? "Searching..." : "Find Nearby"}
        </button>

        {/* ‚úÖ NEW BUTTON */}
        <button onClick={getLiveLocation} style={{ marginLeft: "10px" }}>
          üìç Use My Location
        </button>
      </div>

      <p style={{ fontSize: "12px", marginTop: "10px" }}>
        Location and distance shown are approximate and for educational purposes
        only.
      </p>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {loading && <p>Fetching nearby medical facilities...</p>}

      {results.length > 0 && (
        <div style={{ margin: "15px 0" }}>
          <span style={tabStyle("All")} onClick={() => setSelectedType("All")}>
            All
          </span>
          <span
            style={tabStyle("Hospital")}
            onClick={() => setSelectedType("Hospital")}
          >
            Hospitals
          </span>
          <span
            style={tabStyle("Clinic")}
            onClick={() => setSelectedType("Clinic")}
          >
            Clinics
          </span>
          <span
            style={tabStyle("Pharmacy")}
            onClick={() => setSelectedType("Pharmacy")}
          >
            Pharmacies
          </span>
        </div>
      )}

      <div style={{ marginTop: "20px" }}>
        {!loading && filteredResults.length === 0 && !error && (
          <p>No results found.</p>
        )}

        {filteredResults.map((item, index) => (
          <div
            key={index}
            style={{
              border: "1px solid #e0e0e0",
              padding: "14px",
              marginBottom: "12px",
              borderRadius: "8px",
              backgroundColor: "#fff",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              <strong>{item.name}</strong>
              <span
                style={{
                  backgroundColor: badgeColor(item.type),
                  color: "#fff",
                  padding: "2px 10px",
                  borderRadius: "12px",
                  fontSize: "12px",
                }}
              >
                {item.type}
              </span>
            </div>

            <p style={{ margin: "6px 0" }}>{item.address}</p>

            {item.distance && (
              <p style={{ margin: "4px 0", fontWeight: "500" }}>
                üìè {item.distance} km away
              </p>
            )}

            <p style={{ margin: 0 }}>
              üìû{" "}
              {item.phone !== "Not available" ? (
                <a href={`tel:${item.phone}`}>{item.phone}</a>
              ) : (
                "Phone not available"
              )}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
