import { useState } from "react";

export default function Nearby() {
  const [location, setLocation] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const findNearby = async () => {
    if (!location) {
      setError("Please enter a location");
      return;
    }

    setLoading(true);
    setError("");
    setResults([]);

    try {
      // 1ï¸âƒ£ Convert location â†’ lat & lon (Nominatim)
      const geoRes = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
          location
        )}`
      );

      const geoData = await geoRes.json();

      if (!geoData.length) {
        throw new Error("Location not found");
      }

      const lat = geoData[0].lat;
      const lon = geoData[0].lon;

      // 2ï¸âƒ£ Overpass query for hospitals
      const query = `
        [out:json];
        (
          node["amenity"="hospital"](around:5000,${lat},${lon});
          way["amenity"="hospital"](around:5000,${lat},${lon});
          relation["amenity"="hospital"](around:5000,${lat},${lon});
        );
        out tags;
      `;

      const overpassRes = await fetch(
        "https://overpass-api.de/api/interpreter",
        {
          method: "POST",
          body: query,
        }
      );

      const overpassData = await overpassRes.json();

      const hospitals = overpassData.elements.map((item) => ({
        name: item.tags?.name || "Unnamed Hospital",
        phone:
          item.tags?.phone ||
          item.tags?.["contact:phone"] ||
          "Phone not available",
        address:
          item.tags?.["addr:full"] ||
          `${item.tags?.["addr:street"] || ""} ${
            item.tags?.["addr:city"] || ""
          }`.trim() ||
          "Address not available",
      }));

      setResults(hospitals);
    } catch (err) {
      setError(err.message);
    }

    setLoading(false);
  };

  return (
    <div className="container">
      <h2>Nearby Doctor / Hospital Finder</h2>

      <input
        type="text"
        placeholder="Enter city / area / pincode"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />

      <button onClick={findNearby} disabled={loading}>
        {loading ? "Searching..." : "Find Nearby"}
      </button>

      <p style={{ fontSize: "12px", marginTop: "10px" }}>
        Location and distance shown are approximate and for educational purposes
        only.
      </p>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <div style={{ marginTop: "20px" }}>
        {results.length === 0 && !loading && <p>No hospitals found</p>}

        {results.map((item, index) => (
          <div
            key={index}
            style={{
              border: "1px solid #ddd",
              padding: "10px",
              marginBottom: "10px",
              borderRadius: "6px",
            }}
          >
            <strong>{item.name}</strong>
            <p>{item.address}</p>
            <p>ðŸ“ž {item.phone}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
