import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";

export default function Signup({ setPage }) {
  const { signup, googleLogin } = useContext(AuthContext);

  const [firstName, setFirstName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState("");

  const getPasswordStrength = (value) => {
    if (value.length < 6) return "Weak";
    if (
      value.length >= 8 &&
      /[A-Z]/.test(value) &&
      /[0-9]/.test(value) &&
      /[^A-Za-z0-9]/.test(value)
    )
      return "Strong";
    return "Medium";
  };

  const handleSignup = async () => {
    if (!firstName || !email || !password) {
      alert("Please fill all fields");
      return;
    }

    try {
      await signup(email, password, firstName);
      setPage("home");
    } catch {
      alert("Signup failed");
    }
  };

  return (
    <div className="auth-page">
      <h2>Create Account</h2>
      <p className="auth-subtitle">Create your account to continue</p>

      <input
        type="text"
        placeholder="First name"
        value={firstName}
        onChange={(e) => setFirstName(e.target.value)}
      />

      <input
        type="email"
        placeholder="Email address"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type={showPassword ? "text" : "password"}
        placeholder="Create password"
        value={password}
        onChange={(e) => {
          setPassword(e.target.value);
          setPasswordStrength(getPasswordStrength(e.target.value));
        }}
      />

      {/* Password strength */}
      {password && (
        <div className={`password-strength ${passwordStrength.toLowerCase()}`}>
          Strength: {passwordStrength}
        </div>
      )}

      {/* Show password â€“ LEFT aligned & clean */}
      <div className="show-password-row">
        <label className="show-password-label">
          <input
            type="checkbox"
            checked={showPassword}
            onChange={() => setShowPassword(!showPassword)}
          />
          <span>Show password</span>
        </label>
      </div>

      <button className="auth-btn primary" onClick={handleSignup}>
        Sign up
      </button>

      <button className="auth-btn google" onClick={googleLogin}>
        Sign up with Google
      </button>

      <div className="auth-footer">
        Already have an account?{" "}
        <button className="link-btn" onClick={() => setPage("login")}>
          Login
        </button>
      </div>
    </div>
  );
}
