import React, { useEffect, useState } from "react";

function Feedback() {
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [feedbackList, setFeedbackList] = useState([]);

  // ------------------------
  // SUBMIT FEEDBACK
  // ------------------------
  const submitFeedback = async () => {
    await fetch("http://localhost:3000/feedback", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ rating, comment }),
    });

    setComment("");
    loadFeedback();
  };

  // ------------------------
  // LOAD FEEDBACK
  // ------------------------
  const loadFeedback = async () => {
    const res = await fetch("http://localhost:3000/feedback");
    const data = await res.json();
    setFeedbackList(data);
  };

  useEffect(() => {
    loadFeedback();
  }, []);

  // ------------------------
  // AVERAGE RATING (SAFE)
  // ------------------------
  const averageRating =
    feedbackList.length === 0
      ? 0
      : feedbackList.reduce((sum, f) => sum + Number(f.rating), 0) /
        feedbackList.length;

  // ------------------------
  // JSX
  // ------------------------
  return (
    <div className="feedback-container">
      <div className="feedback-card">
        <h2>Feedback</h2>

        <label>Rate Us:</label>
        <select
          value={rating}
          onChange={(e) => setRating(Number(e.target.value))}
        >
          <option value={1}>⭐</option>
          <option value={2}>⭐⭐</option>
          <option value={3}>⭐⭐⭐</option>
          <option value={4}>⭐⭐⭐⭐</option>
          <option value={5}>⭐⭐⭐⭐⭐</option>
        </select>

        <textarea
          placeholder="Your comments (optional)"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />

        <button onClick={submitFeedback}>Submit Feedback</button>

        <hr />

        {/* FEEDBACK SUMMARY */}
        <div className="feedback-summary">
          <h3>Feedback Summary</h3>

          <p>
            Average Rating: <strong>{averageRating.toFixed(1)}</strong> ⭐
          </p>

          <ul>
            {feedbackList.map((f, index) => (
              <li key={index} className="result">
                <strong>{f.rating} ⭐</strong> —{" "}
                {f.comment || "No comment"}
                <br />
                <small>{f.date}</small>
              </li>
            ))}
          </ul>

          <p className="disclaimer">
            Feedback is collected for educational evaluation only.
          </p>
        </div>
      </div>
    </div>
  );
}

export default Feedback;
