import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";

export default function Login() {
  const { login, googleLogin } = useAuth();
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!email || !password) {
      setMessage("❌ Please fill in all fields.");
      return;
    }

    try {
      await login(email, password);
      setMessage("✅ Login successful.");
      navigate("/home");
    } catch (error) {
      if (error.code === "auth/user-not-found") {
        setMessage("❌ No account found. Please sign up.");
      } else if (error.code === "auth/wrong-password") {
        setMessage("❌ Incorrect password.");
      } else if (error.code === "auth/email-not-verified") {
        setMessage("⚠️ Please verify your email before logging in.");
      } else {
        setMessage("❌ " + (error.message || "Login failed. Try again."));
      }
    }
  };

  const handleGoogle = async () => {
    setMessage("");
    try {
      await googleLogin();
      navigate("/home");
    } catch (err) {
      setMessage("❌ Google login failed.");
    }
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1>Login</h1>

      <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
        <div style={{ marginBottom: 10 }}>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ width: "100%", padding: 8 }}
          />
        </div>

        <div style={{ marginBottom: 10 }}>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: "100%", padding: 8 }}
          />
        </div>

        {message && <p style={{ marginBottom: 8 }}>{message}</p>}

        <button type="submit">Login</button>
        <button type="button" onClick={handleGoogle} style={{ marginLeft: 8 }}>
          Login with Google
        </button>
      </form>

      <p style={{ marginTop: 16 }}>
        Don’t have an account? <Link to="/signup">Sign up</Link>
      </p>
    </div>
  );
}
