import { useLocation } from "react-router-dom";
import { useState } from "react";

export default function Appointment() {
  const { state } = useLocation();
  const hospital = state?.hospital;

  const [formData, setFormData] = useState({
    patientName: "",
    age: "",
    gender: "",
    phone: "",
    date: "",
    time: "",
    department: "",
    symptoms: "",
  });

  const [submitted, setSubmitted] = useState(false);

  if (!hospital) {
    return <p style={{ padding: "20px" }}>No hospital selected</p>;
  }

  // Different departments per hospital
  const departmentMap = {
    hospital: ["General Medicine", "Cardiology", "Orthopedics", "Pediatrics"],
    clinic: ["General Consultation", "ENT", "Dermatology"],
    pharmacy: ["Prescription Pickup", "Consultation"],
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const payload = {
      hospitalName: hospital.name,
      hospitalType: hospital.type,
      ...formData,
      createdAt: new Date().toISOString(),
    };

    // Simulated hospital acceptance
    console.log("Appointment sent to hospital:", payload);

    setSubmitted(true);
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>Book Appointment</h2>

      <p>
        <strong>{hospital.name}</strong>
        <br />
        {hospital.address}
      </p>

      {submitted ? (
        <div
          style={{
            padding: "15px",
            background: "#e6fffa",
            borderRadius: "8px",
          }}
        >
          <h3>Appointment Submitted</h3>
          <p>
            Your appointment request has been sent to{" "}
            <strong>{hospital.name}</strong>.
          </p>
          <p>
            Please arrive on <strong>{formData.date}</strong> at{" "}
            <strong>{formData.time}</strong>.
          </p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            name="patientName"
            placeholder="Patient Name"
            value={formData.patientName}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          />

          <input
            name="age"
            type="number"
            placeholder="Age"
            value={formData.age}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          />

          <select
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          >
            <option value="">Select Gender</option>
            <option>Male</option>
            <option>Female</option>
            <option>Other</option>
          </select>

          <input
            name="phone"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          />

          <select
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          >
            <option value="">Select Department</option>
            {departmentMap[hospital.type]?.map((d) => (
              <option key={d}>{d}</option>
            ))}
          </select>

          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          />

          <input
            type="time"
            name="time"
            value={formData.time}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          />

          <textarea
            name="symptoms"
            placeholder="Describe symptoms"
            value={formData.symptoms}
            onChange={handleChange}
            style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
          />

          <button
            type="submit"
            style={{
              width: "100%",
              padding: "12px",
              background: "#2563eb",
              color: "#fff",
              borderRadius: "6px",
              border: "none",
            }}
          >
            Submit Appointment
          </button>
        </form>
      )}
    </div>
  );
}
