import { useState } from "react";
import { auth } from "../firebase";

function AdminUpdates() {
  const [form, setForm] = useState({
    hospitalName: "",
    location: "",
    title: "",
    description: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitUpdate = async () => {
    const user = auth.currentUser;
    if (!user) return;

    const res = await fetch("http://localhost:5000/api/hospital-updates", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        email: user.email,
        ...form,
      }),
    });

    if (res.ok) {
      setMessage("Hospital update posted successfully.");
      setForm({
        hospitalName: "",
        location: "",
        title: "",
        description: "",
      });
    } else {
      setMessage("You are not authorized to post updates.");
    }
  };

  return (
    <div style={{ padding: "30px" }}>
      <h2>Admin â€“ Post Hospital Update</h2>

      <input
        name="hospitalName"
        placeholder="Hospital Name"
        value={form.hospitalName}
        onChange={handleChange}
        style={styles.input}
      />
      <input
        name="location"
        placeholder="Location (City, State, India)"
        value={form.location}
        onChange={handleChange}
        style={styles.input}
      />
      <input
        name="title"
        placeholder="Update Title"
        value={form.title}
        onChange={handleChange}
        style={styles.input}
      />
      <textarea
        name="description"
        placeholder="Update Description"
        value={form.description}
        onChange={handleChange}
        style={styles.textarea}
      />

      <button onClick={submitUpdate} style={styles.btn}>
        Publish Update
      </button>

      {message && <p>{message}</p>}
    </div>
  );
}

const styles = {
  input: {
    display: "block",
    width: "100%",
    padding: "10px",
    marginBottom: "10px",
  },
  textarea: {
    display: "block",
    width: "100%",
    height: "120px",
    padding: "10px",
    marginBottom: "10px",
  },
  btn: {
    padding: "10px 20px",
    backgroundColor: "#2563eb",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  },
};

export default AdminUpdates;
