import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function NearbyFinder() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [userPos, setUserPos] = useState(null);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!navigator.geolocation) return;

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setUserPos({
          lat: pos.coords.latitude,
          lon: pos.coords.longitude,
        });
      },
      () => {}
    );
  }, []);

  const haversine = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = ((lat2 - lat1) * Math.PI) / 180;
    const dLon = ((lon2 - lon1) * Math.PI) / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos((lat1 * Math.PI) / 180) *
        Math.cos((lat2 * Math.PI) / 180) *
        Math.sin(dLon / 2) ** 2;
    return (R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a))).toFixed(2);
  };

  const fetchNearby = async () => {
    setError("");
    setResults([]);

    try {
      const res = await fetch("https://overpass-api.de/api/interpreter", {
        method: "POST",
        body: `
            [out:json];
            node["amenity"~"hospital|clinic|pharmacy"](around:15000);
            out;
          `,
      });

      if (!res.ok) throw new Error("Overpass API blocked");

      const data = await res.json();

      const formatted = data.elements.map((el) => {
        let distance = null;
        if (userPos) {
          distance = haversine(userPos.lat, userPos.lon, el.lat, el.lon);
        }

        return {
          name: el.tags?.name || "Unnamed",
          lat: el.lat,
          lon: el.lon,
          address: el.tags?.["addr:full"] || "Address not available",
          phone: el.tags?.phone || "Phone not available",
          distance,
        };
      });

      setResults(formatted);
    } catch (err) {
      setError("Overpass API blocked the request. Please try again later.");
    }
  };

  return (
    <div style={{ padding: "16px" }}>
      <h2>Nearby Doctor / Hospital Finder</h2>

      <input
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter city / area / pincode"
        style={{ width: "100%", padding: "8px", marginBottom: "10px" }}
      />

      <button onClick={fetchNearby}>Find Nearby</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {results.map((item, index) => (
        <div
          key={index}
          style={{
            border: "1px solid #ddd",
            padding: "10px",
            marginTop: "10px",
            borderRadius: "6px",
          }}
        >
          <strong>{item.name}</strong>
          <p>{item.address}</p>
          <p>{item.phone}</p>

          {item.distance && (
            <p>
              Distance: <strong>{item.distance} km</strong>
            </p>
          )}

          <button
            onClick={() => navigate("/map", { state: { hospital: item } })}
          >
            View on App Map
          </button>

          <a
            href={`https://www.google.com/maps/dir/?api=1&destination=${item.lat},${item.lon}`}
            target="_blank"
            rel="noreferrer"
            style={{ marginLeft: "10px" }}
          >
            Open in Maps
          </a>
        </div>
      ))}
    </div>
  );
}
