import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";
import "../styles/auth.css";

export default function Signup() {
  const { signup } = useAuth();
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      await signup(email, password);
      setMessage("✅ Signup successful! Check your email to verify.");
      setTimeout(() => navigate("/"), 2000);
    } catch (error) {
      if (error.code === "auth/email-already-in-use") {
        setMessage("❌ Email already in use. Please login or use a different email.");
      } else if (error.code === "auth/weak-password") {
        setMessage("❌ Password is too weak. Use at least 6 characters.");
      } else if (error.code === "auth/invalid-email") {
        setMessage("❌ Invalid email address.");
      } else {
        setMessage("❌ Signup failed: " + (error.message || "Try again."));
      }
    }
  };

  return (
    <div className="auth-box">
      <h2>AI Health Assistant</h2>

      {message && (
        <p style={{ 
          marginBottom: "10px", 
          color: message.includes("✅") ? "#5cb85c" : "#d9534f",
          fontWeight: "bold"
        }}>
          {message}
        </p>
      )}

      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <input
          type={showPassword ? "text" : "password"}
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <div style={{ textAlign: "left", marginBottom: "10px" }}>
          <input
            type="checkbox"
            checked={showPassword}
            onChange={() => setShowPassword(!showPassword)}
          />{" "}
          Show password
        </div>

        <button type="submit">Sign up</button>
      </form>

      <p>
        Already have an account? <Link to="/">Login</Link>
      </p>
    </div>
  );
}
