import { useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();

  return (
    <div style={styles.container}>
      {/* Welcome Section */}
      <h1 style={styles.title}>Welcome to AI Health Assistant</h1>
      <p style={styles.subtitle}>
        Your personal assistant for general health guidance and emergency help.
      </p>

      {/* Cards Section */}
      <div style={styles.cardContainer}>
        {/* Health Guidance Card */}
        <div style={styles.card} onClick={() => navigate("/health-chat")}>
          <h2>ðŸ©º Health Guidance</h2>
          <p>Ask health-related questions and get general medical guidance.</p>
        </div>

        {/* Emergency Card */}
        <div
          style={{ ...styles.card, ...styles.emergencyCard }}
          onClick={() => navigate("/emergency")}
        >
          <h2>ðŸš¨ Emergency</h2>
          <p>Get immediate help and contact nearby hospitals in emergencies.</p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: "40px",
    textAlign: "center",
  },
  title: {
    fontSize: "32px",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "16px",
    color: "#555",
    marginBottom: "40px",
  },
  cardContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "30px",
    flexWrap: "wrap",
  },
  card: {
    width: "260px",
    padding: "25px",
    borderRadius: "12px",
    backgroundColor: "#f5f7fa",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    cursor: "pointer",
    transition: "transform 0.2s",
  },
  emergencyCard: {
    backgroundColor: "#ffecec",
  },
};

export default Home;
