import React, { useEffect, useState } from "react";

function Feedback() {
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [feedbackList, setFeedbackList] = useState([]);
  const isAdmin=true;

  // ------------------------
  // LOAD FEEDBACK
  // ------------------------
  const loadFeedback = async () => {
    try {
      const res = await fetch("http://localhost:5000/feedback");
      const data = await res.json();
      setFeedbackList(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Failed to load feedback", err);
    }
  };

  // ------------------------
  // SUBMIT FEEDBACK
  // ------------------------
  const submitFeedback = async () => {
    if (rating === 0) return; // prevent empty rating

    try {
      await fetch("http://localhost:5000/feedback", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          rating: Number(rating),
          comment,
          date: new Date().toLocaleDateString(),
        }),
      });

      setRating(0);
      setComment("");
      loadFeedback(); // refresh list
    } catch (err) {
      console.error("Failed to submit feedback", err);
    }
  };

  useEffect(() => {
    loadFeedback();
  }, []);

  // ------------------------
  // AVERAGE RATING
  // ------------------------
  const averageRating =
    feedbackList.length === 0
      ? 0
      : feedbackList.reduce((sum, f) => sum + Number(f.rating || 0), 0) /
        feedbackList.length;
        const deleteFeedback = async (id) => {
  if (!window.confirm("Delete this feedback?")) return;

  await fetch(`http://localhost:3000/feedback/${id}`, {
    method: "DELETE",
  });

  loadFeedback();
};


  // ------------------------
  // JSX
  // ------------------------
  return (
    <div className="feedback-container">
      <div className="feedback-card">
        <h2>Feedback</h2>

        <label>Rate Us:</label>
        <div className="star-rating">
  {[1, 2, 3, 4, 5].map((star) => (
    <span
      key={star}
      className={star <= rating ? "star active" : "star"}
      onClick={() => setRating(star)}
    >
      ★
    </span>
  ))}
</div>


        <textarea
          placeholder="Your comments (optional)"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />

        <button onClick={submitFeedback}>Submit Feedback</button>

        <hr />

        {/* FEEDBACK SUMMARY */}
        <div className="feedback-summary">
          <h3>Feedback Summary</h3>

          <p>
            Average Rating:{" "}
            <strong>{averageRating.toFixed(1)}</strong> ⭐
          </p>

          <ul>
            {feedbackList.map((f, index) => (
  <li key={index} className="result">
    <div className="feedback-row">
      <div>
        <strong>{f.rating} ⭐</strong> — {f.comment || "No comment"}
        <br />
        <small>{f.date}</small>
      </div>

      {isAdmin && (
        <button
          className="delete-btn"
          onClick={() => deleteFeedback(f.id)}
        >
          ❌
        </button>
      )}
    </div>
  </li>
))}

          </ul>

          <p className="disclaimer">
            Feedback is collected for educational evaluation only.
          </p>
        </div>
      </div>
    </div>
  );
}

export default Feedback;
