import React, { useState } from "react";

function HealthChat() {
  const [image, setImage] = useState(null);
  const [category, setCategory] = useState("skin");
  const [result, setResult] = useState(null);

  const uploadImage = async () => {
    if (!image) {
      alert("Please upload an image");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("category", category);

    const response = await fetch("http://localhost:3000/image-analyze", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    setResult(data);
  };

  return (
    <div className="page">
      <h2>Image-Based Health Guidance</h2>

      <input type="file" onChange={(e) => setImage(e.target.files[0])} />

      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="skin">Skin</option>
        <option value="wound">Wound</option>
        <option value="other">Other</option>
      </select>

      <button onClick={uploadImage}>Analyze Image</button>

      {result && (
        <div className="result">
          <p><b>Category:</b> {result.category}</p>
          <p><b>Severity:</b> {result.severity}</p>
          <p><b>Advice:</b> {result.advice}</p>
          <p className="disclaimer">{result.disclaimer}</p>
        </div>
      )}
    </div>
  );
}

export default HealthChat;
