import { useNavigate } from "react-router-dom";

/* existing imports remain */

export default function NearbyFinder() {
  const navigate = useNavigate();

  const filteredResults = results;

  /* existing state, effects, logic remain */

  const openInAppMap = (item) => {
    if (!item.lat || !item.lon) {
      alert("Location not available for this place");
      return;
    }

    navigate("/map", {
      state: {
        hospital: {
          name: item.name,
          lat: item.lat,
          lon: item.lon,
          address: item.address,
          phone: item.phone,
          type: item.type,
        },
      },
    });
  };

  return (
    <div>
      {/* existing UI stays */}

      {filteredResults.map((item, index) => (
        <div
          key={index}
          style={{
            border: "1px solid #ddd",
            padding: "12px",
            marginBottom: "12px",
            borderRadius: "8px",
          }}
        >
          <strong>{item.name}</strong>
          <p>{item.address || "Address not available"}</p>
          <p>{item.phone || "Phone not available"}</p>

          {/* EXISTING Open in Google Maps — DO NOT REMOVE */}
          {item.lat && item.lon && (
            <a
              href={`https://www.google.com/maps/dir/?api=1&destination=${item.lat},${item.lon}`}
              target="_blank"
              rel="noreferrer"
              style={{ marginRight: "12px" }}
            >
              Open in Maps
            </a>
          )}

          {/* NEW — View inside app */}
          <button
            onClick={() => openInAppMap(item)}
            style={{
              padding: "6px 10px",
              borderRadius: "6px",
              border: "none",
              background: "#2563eb",
              color: "#fff",
              cursor: "pointer",
            }}
          >
            View on App Map
          </button>
        </div>
      ))}
    </div>
  );
}
