import { useState, useRef, useEffect } from "react";
import Header from "../components/Header";

const BOT_IMG = "https://cdn-icons-png.flaticon.com/512/4712/4712109.png";

function HealthChat() {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([
    {
      from: "bot",
      text: "Hello! I am your Health Assistant. I provide general health guidance (not medical diagnosis). How can I help you?",
    },
  ]);
  const [isTyping, setIsTyping] = useState(false);

  const chatEndRef = useRef(null);

  // Auto-scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const userText = message;
    setMessages((prev) => [...prev, { from: "user", text: userText }]);
    setMessage("");
    setIsTyping(true);

    try {
      const res = await fetch("http://localhost:5000/api/health-guidance", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: userText }),
      });

      const data = await res.json();

      setMessages((prev) => [...prev, { from: "bot", text: data.reply }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        {
          from: "bot",
          text: "Unable to provide guidance right now. Please try again.",
        },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <>
      <Header />
      <div style={{ padding: "20px" }}>
        <h2>Health Guidance</h2>

        <div
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            borderRadius: "8px",
            height: "360px",
            overflowY: "auto",
            marginBottom: "10px",
          }}
        >
          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                display: "flex",
                justifyContent: m.from === "user" ? "flex-end" : "flex-start",
                marginBottom: "10px",
              }}
            >
              {m.from === "bot" && (
                <img
                  src={BOT_IMG}
                  alt="bot"
                  style={{
                    width: "32px",
                    height: "32px",
                    marginRight: "8px",
                  }}
                />
              )}

              <div
                style={{
                  padding: "10px",
                  borderRadius: "8px",
                  background: m.from === "user" ? "#2c5364" : "#eee",
                  color: m.from === "user" ? "#fff" : "#000",
                  maxWidth: "70%",
                }}
              >
                {m.text}
              </div>
            </div>
          ))}

          {/* Typing Indicator */}
          {isTyping && (
            <div style={{ display: "flex", alignItems: "center" }}>
              <img
                src={BOT_IMG}
                alt="bot"
                style={{
                  width: "32px",
                  height: "32px",
                  marginRight: "8px",
                }}
              />
              <div
                style={{
                  background: "#eee",
                  padding: "10px",
                  borderRadius: "8px",
                  fontStyle: "italic",
                  color: "#555",
                }}
              >
                Typing<span className="dots">...</span>
              </div>
            </div>
          )}

          <div ref={chatEndRef} />
        </div>

        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Describe your symptoms..."
          style={{ width: "80%", padding: "10px" }}
        />

        <button
          onClick={sendMessage}
          style={{ padding: "10px 20px", marginLeft: "10px" }}
        >
          Send
        </button>
      </div>
    </>
  );
}

export default HealthChat;
