import { Routes, Route, useLocation } from "react-router-dom";
import { useAuth } from "./context/AuthContext";

function AppRoutes() {
  const { user, loading } = useAuth();
  const location = useLocation();

  if (loading) {
    return <div style={{ padding: "30px" }}>Loading...</div>;
  }

  const hideNavbar =
    location.pathname === "/" || location.pathname === "/signup";

  return (
    <>
      {/* âœ… Navbar only when logged in AND not on auth pages */}
      {user && !hideNavbar && <Navbar />}

      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />

        <Route
          path="/home"
          element={
            <ProtectedRoute>
              <Home />
            </ProtectedRoute>
          }
        />

        <Route
          path="/health-chat"
          element={
            <ProtectedRoute>
              <HealthChat />
            </ProtectedRoute>
          }
        />

        <Route
          path="/nearby"
          element={
            <ProtectedRoute>
              <NearbyFinder />
            </ProtectedRoute>
          }
        />

        <Route
          path="/emergency"
          element={
            <ProtectedRoute>
              <Emergency />
            </ProtectedRoute>
          }
        />

        <Route
          path="/admin"
          element={
            <AdminRoute>
              <AdminUpdates />
            </AdminRoute>
          }
        />
      </Routes>
    </>
  );
}
