import React, { useState } from "react";
import doctorsData from "../data/doctors";

function NearbyFinder() {
  const [location, setLocation] = useState("");
  const [specialization, setSpecialization] = useState("All");
  const [results, setResults] = useState([]);

  const findNearby = () => {
    let filtered = doctorsData.filter((doc) =>
      doc.location.toLowerCase().includes(location.toLowerCase())
    );

    if (specialization !== "All") {
      filtered = filtered.filter(
        (doc) => doc.specialization === specialization
      );
    }

    filtered.sort((a, b) => a.distance - b.distance);
    setResults(filtered);
  };

  return (
    <div className="page">
      <h2>Nearby Doctor / Hospital Finder</h2>

      <input
        placeholder="Enter your location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />

      <select
        value={specialization}
        onChange={(e) => setSpecialization(e.target.value)}
      >
        <option value="All">All</option>
        <option value="General">General</option>
        <option value="Cardiology">Cardiology</option>
        <option value="Dermatology">Dermatology</option>
        <option value="Orthopedics">Orthopedics</option>
      </select>

      <button onClick={findNearby}>Find Nearby</button>

      <ul>
        {results.map((doc, index) => (
          <li key={index} className="result">
            <strong>{doc.name}</strong> ({doc.type})<br />
            Specialization: {doc.specialization}<br />
            Distance: {doc.distance} km
          </li>
        ))}
      </ul>

      <p className="disclaimer">
        Location and distance shown are approximate and for educational
        purposes only.
      </p>
    </div>
  );
}

export default NearbyFinder;
