import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

export default function Map() {
  const location = useLocation();
  const hospital = location.state?.hospital;

  const [userPos, setUserPos] = useState(null);
  const [mapMode, setMapMode] = useState("hospital");
  const [error, setError] = useState("");

  useEffect(() => {
    if (!navigator.geolocation) {
      setError("Geolocation not supported");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setUserPos({
          lat: pos.coords.latitude,
          lon: pos.coords.longitude,
        });
      },
      () => {
        setError("Location permission denied");
      }
    );
  }, []);

  // helper: create zoomed-in bbox
  const createBBox = (lat, lon, zoom = 0.01) => {
    return `${lon - zoom},${lat - zoom},${lon + zoom},${lat + zoom}`;
  };

  let mapUrl = "https://www.openstreetmap.org/export/embed.html";

  if (mapMode === "user" && userPos) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?bbox=${createBBox(
      userPos.lat,
      userPos.lon
    )}&marker=${userPos.lat},${userPos.lon}`;
  }

  if (mapMode === "hospital" && hospital) {
    mapUrl = `https://www.openstreetmap.org/export/embed.html?bbox=${createBBox(
      hospital.lat,
      hospital.lon
    )}&marker=${hospital.lat},${hospital.lon}`;
  }

  return (
    <div style={{ padding: "16px" }}>
      <h2>Map View</h2>

      {hospital && (
        <div style={{ marginBottom: "10px" }}>
          <strong>{hospital.name}</strong>
          <p>{hospital.address || "Address not available"}</p>
          <p>{hospital.phone || "Phone not available"}</p>
        </div>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}

      <div style={{ marginBottom: "10px" }}>
        <button
          onClick={() => setMapMode("hospital")}
          style={{
            marginRight: "10px",
            padding: "6px 12px",
            backgroundColor: "#2563eb",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          Hospital Location
        </button>

        <button
          onClick={() => setMapMode("user")}
          style={{
            padding: "6px 12px",
            backgroundColor: "#059669",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          My Location
        </button>
      </div>

      <iframe
        title="map"
        width="100%"
        height="450"
        frameBorder="0"
        src={mapUrl}
        style={{ borderRadius: "10px" }}
      />

      {hospital && (
        <div style={{ marginTop: "10px" }}>
          <a
            href={`https://www.google.com/maps/dir/?api=1&destination=${hospital.lat},${hospital.lon}`}
            target="_blank"
            rel="noreferrer"
          >
            Open Route in Google Maps
          </a>
        </div>
      )}
    </div>
  );
}
